krpano.actions.test_obrefinestra = function openWin() {
	myWindow = window.open(document.URL, "", "width=0, height=0");

}

krpano.actions.test_resizefinestra_res = function resizeWinTo(arg1, arg2) {
	myWindow.resizeTo(arg1 ,arg2);
	myWindow.focus();
}
